	private static void findPath (List<Vector3> pathPts)
	{
		int iterations = 6;//increase to increase number of points on path
		for (int i=0; i<iterations; i++)
		{
			int k=0;
			int numPoints = pathPts.Count;
			for(int j=0;j<numPoints-1;j++)
			{
				Vector3 newVect = (pathPts[k]+pathPts[k+1])/2;
				//Debug.Log(newVect);
				pathPts.Insert(k+1,newVect);
				k+=2;
			}
		}
	}
	//Distance b/w consecutive points
	public static float getStepDistance()
	{
		if (stepPath < 0.0f)
			Debug.LogError ("Distance b/w consecutive points not set.");
		return stepPath;
	}
